import React, { useState } from 'react';
import styled from 'styled-components';
import data from './data';
import { nanoid } from 'nanoid';


function App() {
  const [parargraph, setParargraph] = useState(data);
  const [value, setValue] = useState('');


  const handleSubmit=(e)=>{
    e.preventDefault();

    console.log(value);

    if(value>parargraph.length){
      setValue(parargraph.length)
    }

    setParargraph(data.slice(0,value));

  }



  return <Wrapper>

    <div className="section-center">

      <h4 className='title'>Tired of boring lorem ipsum?</h4>

      <form onSubmit={handleSubmit}>

        <div className="form-control">

          <label htmlFor="paragraph " >paragraph:</label>
          <input type="number" name="number" id="paragraph" value={value} onChange={(e)=>setValue(parseInt(e.target.value))} max={data.length} min={1}/>
          <button type="submit">Generate</button>

        </div>

        <div className="result">
          {
            parargraph.map((item)=>{
              return <p key={nanoid()}>{item}</p>
            })
          }
        </div>


      </form>

    </div>



  </Wrapper>


}


const Wrapper=styled.div`

.section-center{
  padding:2rem;

  .title{
    font-size:1.1rem;
    font-weight: bold;
    margin:1rem 0;
    text-transform:uppercase;
  }

  .form-control{
    text-align: center;
    margin-bottom:1rem;
  }

  label,input,button{
    font-size:1.2rem;
    text-transform: capitalize;
    margin: 0 0.2rem;
    padding:0.5rem 0.rem;
    border-radius:5px;
    border: none;
  }





}





`








export default App;
