import React, { useState } from 'react';
import styled from 'styled-components';
import data from './data';
import { nanoid } from 'nanoid';


function App() {
  const [parargraph, setParargraph] = useState(data);
  const [value, setValue] = useState(0);


  const handleSubmit=(e)=>{
    e.preventDefault();

    if(value>parargraph.length){
      setValue(parargraph.length)
    }

    setParargraph(data.slice(0,value));

  }



  return <Wrapper>

    <div className="section-center">

      <h4>Tired of boring lorem ipsum</h4>

      <form onSubmit={handleSubmit}>

        <div className="form-control">

          <label htmlFor="paragraph ">paragraph:</label>
          <input type="number" name="number" id="paragraph" value={value} onChange={(e)=>setValue(parseInt(e.target.value))} max={parargraph.length} min={1}/>
          <button type="submit">Generate</button>

        </div>

        <div className="result">
          {
            parargraph.map((item)=>{
              return <p key={nanoid()}>{item}</p>
            })
          }
        </div>


      </form>

    </div>



  </Wrapper>


}


const Wrapper=styled.div`





`








export default App;
