import React from 'react'
import styled from 'styled-components'
import { useState } from 'react'
import Values from 'values.js'

const Form = () => {

    const [color, setColor] = useState('#f9b9a8');
    const [ShadeArray, setShadeArray] = useState(new Values(color).all(20))

    let newArray=ShadeArray.map((item)=>{
        const {rgb,weight}=item;
        return {rgb,weight}
    })



    const handleSubmit=(e)=>{
        e.preventDefault();
    }



 return <Wrapper>

     <form onSubmit={handleSubmit}>

         <label htmlFor="color">Color Generator: </label>
         <input type="color" name="color" id="color" value={color} onChange={(e)=>setColor(e.target.value)}/>
          <input type="text" value={color}  placeholder='#f9b9a8' onChange={(e)=>setColor(e.target.value)}/>
         <button type='submit'>submit</button>

     </form>






 </Wrapper>
}

const Wrapper=styled.div`


button{
    color:black;
}





`

export default Form
