import React from 'react'
import styled from 'styled-components'
import { useState } from 'react'
import Values from 'values.js'
import ColorsList from './ColorsList'

const Form = () => {

    const [color, setColor] = useState('#f9b9a8');
    const [ShadeArray, setShadeArray] = useState(new Values(color).all(20))

    let newArray=ShadeArray.map((item)=>{
        const {rgb,weight}=item;
        return {rgb,weight}
    })



    const handleSubmit=(e)=>{
        e.preventDefault();
    }



 return <Wrapper>

     <form onSubmit={handleSubmit}>

         <label htmlFor="color">Color Generator: </label>
         <input type="color" name="color" id="color" value={color} onChange={(e)=>setColor(e.target.value)} className='input-color'/>
         <input type="text" value={color}  placeholder='#f9b9a8' onChange={(e)=>setColor(e.target.value)} className='input-text'/>
         <button type='submit'>submit</button>

     </form>

     <ColorsList newArray={newArray} />




 </Wrapper>
}

const Wrapper=styled.div`

padding:3rem 1rem;

form{
    display: flex;
    align-items: center;
}

label{
    font-size:1.3rem;
}

.input-text,button{

    font-size:1.1rem;
    border: none;

}

.input-color{
    border: transparent;
    width:3rem;
    height:3rem;
    margin-left: 1rem;
}

button{
    color:black;
}








`

export default Form
